import { Router } from '@angular/router';
import { Component, OnInit } from '@angular/core';
import { NavController, MenuController } from '@ionic/angular';
import { SharedService } from '../../services/shared.service';

@Component({
  selector: 'app-menu',
  templateUrl: 'menu.page.html',
  styleUrls: ['menu.page.scss'],
})
export class MenuPage implements OnInit {
  username: string | null = null;
  isMenuOpen = false;

  constructor(
    private router: Router,
    private navController: NavController,
    private sharedService: SharedService,
    private menuController: MenuController
  ) {}

  ngOnInit() {
    this.username = this.sharedService.getUsername();
  }

  irA(destino: string) {
    this.router.navigate([`/menu/${destino}`]);
  }

  toggleMenu() {
    this.isMenuOpen = !this.isMenuOpen;
  }

  closeMenu() {
    if (this.isMenuOpen) {
      this.menuController.close();
    } else {
      this.navController.navigateBack('/'); // Cambia '/menu' por la ruta correcta
    }
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>

    <ion-title>Menú</ion-title>
  </ion-toolbar>
  <ion-toolbar id="username">
    <ion-label>Bienvenido {{ username }}</ion-label>
  </ion-toolbar>
</ion-header>


<ion-menu side="start" contentId="main-content">
  <ion-header>
    <ion-toolbar>
      <ion-title>Menú</ion-title>
    </ion-toolbar>
  </ion-header>

  <ion-content>
    <ion-list>
      <ion-menu-toggle auto-hide="false">
        <ion-item (click)="irA('cuenta')">
          <ion-icon slot="start" name="person"></ion-icon>
          <ion-label>Cuenta</ion-label>
        </ion-item>
      </ion-menu-toggle>
      <ion-menu-toggle auto-hide="false">
        <ion-item (click)="irA('perfil')">
          <ion-icon slot="start" name="person-circle"></ion-icon>
          <ion-label>Perfil</ion-label>
        </ion-item>
      </ion-menu-toggle>
      <ion-menu-toggle auto-hide="false">
        <ion-item (click)="irA('asignaturas')">
          <ion-icon slot="start" name="book"></ion-icon>
          <ion-label>Asignaturas</ion-label>
        </ion-item>
      </ion-menu-toggle>
    </ion-list>
  </ion-content>
</ion-menu>

<ion-content>
  <ion-item id="contenedor">
    <ion-button >Marcar Asistencia</ion-button>
    <ion-button >Cerrar sesion</ion-button>
  </ion-item>
</ion-content>

<ion-router-outlet id="main-content"></ion-router-outlet>
