import { Component } from '@angular/core';
import { ToastController } from '@ionic/angular';
import { Router } from '@angular/router';
import { SharedService } from '../../services/shared.service';
import { SQLite, SQLiteObject} from '@awesome-cordova-plugins/sqlite/ngx';

import { SegmentChangeEventDetail } from '@ionic/core';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {

  segmentValue: string = 'profesor'; // Valor predeterminado para el segmento

  segmentChanged(event: any) {
    console.log('Segment changed to:', event);
    // Puedes realizar acciones adicionales aquí según el segmento seleccionado.
  }

  constructor(
    private router: Router,
    public toastController: ToastController,
    private sharedService: SharedService,
    private sqlite: SQLite
  ) {}
  
  async mostrarMensaje(mensaje: string) {
    const toast = await this.toastController.create({
      message: mensaje,
      duration: 2000,
      position: 'top'
    });
    toast.present();
  }

  async validarYLogin() {
    const usuario = (document.querySelector('input[name="User"]') as HTMLInputElement).value;
    const contrasena = (document.querySelector('input[name="Pass"]') as HTMLInputElement).value;

    if (!usuario || !contrasena) {
      this.mostrarMensaje('Los campos no pueden estar vacíos.');
      this.mostrarMensaje('No paso por base de datos');
      this.router.navigate(['/menu']);
      return;
    }

    this.sqlite.create({
      name: 'usuarios.db',
      location: 'default'
    })
    .then((db: SQLiteObject) => {
      db.executeSql('SELECT * FROM credenciales WHERE usuario = ? AND contrasena = ?', [usuario, contrasena])
        .then(data => {
          if (data.rows.length > 0) {
            // Las credenciales son válidas, redirige a la página /menu
            this.sharedService.setUsername(usuario);
            this.router.navigate(['/menu']);
          } else {
            // Las credenciales son incorrectas, muestra un mensaje de error
            this.mostrarMensaje('Usuario o contraseña incorrectos.');
          }
        })
        .catch(error => {
          console.error('Error al ejecutar consulta en SQLite', error);
          this.mostrarMensaje('Error al verificar las credenciales.');
        });
    })
    .catch(error => {
      console.error('Error al abrir la base de datos SQLite', error);
      this.mostrarMensaje('Error al abrir la base de datos.');
    });
  }


  

  navigateToMenu() {
    this.router.navigate(['/menu']); // Navigate to the "Menu" page
  }

  navigateToForgot() {
    this.router.navigate(['/forgot-password']); // Navigate to the "Forgot" page
  }

}
<ion-header [translucent]="true" id="Header">
  <ion-toolbar>
    <ion-title>LOGIN</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <div class="circle"></div>
  <ion-segment [(ngModel)]="segmentValue" (ionChange)="segmentChanged($event)">
    <ion-segment-button value="profesor">
      Profesor
    </ion-segment-button>
    <ion-segment-button value="alumno">
      Alumno
    </ion-segment-button>
  </ion-segment>
  

  
  <div id="container" class="cssanimation leFadeIn sequence">
    <ion-img src="../../assets/Screenshot 2023-08-16 210220.png"/>
    <ion-item>
      <ion-label position="floating" type="email">Nombre de Usuario</ion-label>
      <ion-input name="User" type="text"></ion-input>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Contraseña</ion-label>
      <ion-input name="Pass" type="password"></ion-input>
    </ion-item>
    <ion-button mat-raised-button (click)="validarYLogin()">Login</ion-button>
    <a (click)="navigateToForgot()">Restablecer Contraseña</a>
  </div>
</ion-content>
