import { NgModule } from '@angular/core';
import { Routes, RouterModule } from '@angular/router';
import { PerfilComponent } from './menu/perfil/perfil.component';
import { MiCuentaComponent } from './menu/mi-cuenta/mi-cuenta.component';
import { AsignaturasComponent } from './menu/asignaturas/asignaturas.component';
import { NotFoundComponent } from './not-found/not-found.component';
import { MenuPage } from './home/ '; // Asegúrate de importar LoginComponent
import { MenuComponent } from './menu/menu.component'; // Asegúrate de importar MenuComponent
import { MenuResolverService } from './menu-resolver.service'; // Asegúrate de importar MenuResolverService

const routes: Routes = [
  { path: '', redirectTo: 'home', pathMatch: 'full' },
  { path: 'home', loadChildren: () => import('./home/home.module').then(m => m.HomePageModule) },
  { path: 'menu', component: MenuComponent, resolve: { menuResolver: MenuResolverService } }, // Agrega la configuración del resolver aquí
  { path: 'menu-profesor', loadChildren: () => import('./menu-profesor/menu-profesor.module').then( m => m.MenuProfesorPageModule) },
  { path: 'forgot-password', loadChildren: () => import('./home/forgot/forgot-password.module').then( m => m.ForgotPasswordPageModule) },
  /* Componentes */
  {
    path: 'perfil',
    component: PerfilComponent
  },
  {
    path: 'cuenta',
    component: MiCuentaComponent
  },
  {
    path: 'asignaturas',
    component: AsignaturasComponent
  },
  /* Not Found */
  {
    path: 'not-found',
    component: NotFoundComponent, 
  },
  {
    path: '**', 
    redirectTo: 'not-found',
  },
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule],
})
export class AppRoutingModule {}
