import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { ToastController } from '@ionic/angular';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {

  constructor(
    private router: Router,
    private http: HttpClient,
    public toastController: ToastController
  ) {}

  async mostrarMensaje(mensaje: string) {
    const toast = await this.toastController.create({
      message: mensaje,
      duration: 2000,
      position: 'top'
    });
    toast.present();
  }

  async validarYLogin() {
    const usuario = (document.querySelector('input[name="User"]') as HTMLInputElement).value;
    const contrasena = (document.querySelector('input[name="Pass"]') as HTMLInputElement).value;

    if (!usuario || !contrasena) {
      const toast = await this.toastController.create({
        message: 'Los campos no pueden estar vacíos.',
        duration: 2000,
        position: 'top'
      });
      toast.present();
      return;
    }

    const apiUrl = 'http://localhost:3000/api/login';

    this.http.post(apiUrl, { usuario, contrasena }).subscribe(
      (response: any) => {
        if (response.message === 'Inicio de sesión exitoso') {
          this.router.navigate(['/menu']); // Cambia 'menu' por la ruta deseada
        } else {
          this.mostrarMensaje('Credenciales inválidas');
        }
      },
      (error) => {
        console.error('Error en la solicitud:', error);
        this.mostrarMensaje('Error en el servidor');
      }
    );
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>Login</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <div id="container">
    <ion-img src="../../assets/"/>
    <ion-item>
      <ion-label position="floating">Usuario</ion-label>
      <ion-input name="User" type="text"></ion-input>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Contraseña</ion-label>
      <ion-input name="Pass" type="password"></ion-input>
    </ion-item>
    <ion-button (click)="validarYLogin()">Login</ion-button>
  </div>
</ion-content>
