import { Injectable } from '@angular/core';
import axios from 'axios';

@Injectable({
  providedIn: 'root',
})
export class EmailValidationService {
  private apiKey = 'f6bb1a7bf37eac28cd3cf2f1c38c8f99cb687de9';

  async validateEmail(email: string): Promise<boolean> {
    try {
      const response = await axios.get(
        `https://api.hunter.io/v2/email-verifier?email=${email}&api_key=${this.apiKey}`
      );

      const verificationResult = response.data.data;

      return verificationResult.result === 'valid';
    } catch (error) {
      console.error('Error al validar el correo electrónico:', error);
      return false;
    }
  }
}
