import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ToastController } from '@ionic/angular'; // Importa el ToastController

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {

  constructor(
    private router: Router,
    public toastController: ToastController // Agrega el ToastController al constructor
  ) {}

  async validarYLogin() {
    // Obtén los valores de los campos de usuario y contraseña
    const usuario = (document.querySelector('input[name="User"]') as HTMLInputElement).value;
    const contrasena = (document.querySelector('input[name="Pass"]') as HTMLInputElement).value;

    // Validar que los campos no estén vacíos
    if (usuario === '' || contrasena === '') {
      const toast = await this.toastController.create({
        message: 'Los campos no pueden estar vacíos.',
        duration: 2000, // Duración del mensaje en milisegundos
        position: 'top' // Posición del mensaje en la pantalla
      });
      toast.present();
      return; // Detener la función si los campos están vacíos
    }

    // Aquí puedes agregar la lógica de autenticación si es necesario

    // Redirige al componente "Menu"
    this.router.navigate(['/menu']); // Asegúrate de que la ruta sea correcta
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>
      Inicia Sesion
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Blank</ion-title>
    </ion-toolbar>
  </ion-header>

  <div id="container">
    <ion-input name="User" type="text" placeholder="Juanito"></ion-input>
    <ion-input name="Pass" type="password" placeholder="*****"></ion-input>
    <ion-button (click)="validarYLogin()">Login</ion-button>
  </div>
</ion-content>
