import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { MenuController } from '@ionic/angular';
import { SharedService } from '../../services/shared.service';
interface MenuItemProfesor {
  label: string;
  icon: string;
  destination: string;
}


@Component({
  selector: 'app-menu-profesor',
  templateUrl: './menu-profesor.page.html',
  styleUrls: ['./menu-profesor.page.scss'],
})
export class MenuProfesorPage implements OnInit {
  username: string | null = null;
  menuItems: MenuItemProfesor[] = [
    { label: 'Inicio', icon: 'Home', destination: 'menu-profesor' },
    { label: 'Perfil', icon: 'person-circle', destination: 'perfil-profesor' },
    { label: 'Asignaturas', icon: 'book', destination: 'asignaturasimpartidas' },
    { label: 'Cerrar Sesión', icon: 'exit', destination: 'Cerrar Sesion' }
  ];

  constructor(
    private router: Router,
    private sharedService: SharedService,
    private menuController: MenuController
  ) {}

  ngOnInit() {
    this.username = this.sharedService.getUsername();
  }

  irA(destino: string) {
    const rutasValidas = ['perfil-profesor', 'asignaturas-impartidas', 'menu-profesor'];
    if (rutasValidas.includes(destino)) {
      this.router.navigate([destino]);
    } else {
      // Puedes redirigir a una página de error o realizar alguna otra acción aquí
      this.router.navigate(['pagina-de-error']);
    }
  }

  cerrarMenu() {
    this.menuController.close();
  }
}
<ion-header [translucent]="true">
  <ion-toolbar id="username">
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-label>Bienvenido Profesor {{username}}</ion-label>
  </ion-toolbar>
</ion-header>

<ion-menu side="start" contentId="main-content">
  <ion-header>
    <ion-toolbar>
      <ion-icon (click)="cerrarMenu()" slot="start" name="arrow-back" id="arrow-back"></ion-icon>
      <ion-label>Menu</ion-label>
    </ion-toolbar>
  </ion-header>

  <ion-content>
    <ion-list>
      <ion-menu-toggle *ngFor="let item of menuItems">
        <ion-item (click)="irA(item.destination)">
          <ion-icon slot="start" [name]="item.icon"></ion-icon>
          <ion-label>{{ item.label }}</ion-label>
        </ion-item>
      </ion-menu-toggle>
    </ion-list>
  </ion-content>
</ion-menu>

<ion-content id="Body">
  <a>Esta es la pagina principal</a>
</ion-content>

<ion-router-outlet id="main-content"></ion-router-outlet>
