import { Component, OnInit } from '@angular/core';
import { FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.page.html',
  styleUrls: ['./forgot-password.page.scss'],
})
export class ForgotPasswordPage implements OnInit {

  constructor() { }

  email: string = ''; // Variable para almacenar el valor del campo de correo electrónico
  emailFormControl = new FormControl('', [
    Validators.required,
    Validators.email,
  ]);
  resetPassword() {
    // Aquí deberías implementar la lógica para enviar instrucciones de restablecimiento de contraseña
    // Puedes acceder al valor del campo de correo electrónico con this.email
    // Por ejemplo, puedes mostrar un mensaje de éxito o enviar una solicitud al servidor para restablecer la contraseña.
  } 

  ngOnInit() {
  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>forgotPassword</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">forgotPassword</ion-title>
    </ion-toolbar>
  </ion-header>
  <form (ngSubmit)="resetPassword()">
    <h2>Forgot Your Password?</h2>
    <p>Enter your email address and we will send you instructions to reset your password.</p>
    
    <ion-item>
      <ion-label position="floating">Email</ion-label>
      <ion-input type="email" id="email" name="email" [(ngModel)]="email" required></ion-input>
    </ion-item>
    
    <button type="submit" [disabled]="!email">Reset Password</button>
  </form>


</ion-content>
