import { Component } from '@angular/core';
import { Platform } from '@ionic/angular';
import { SQLiteService } from 'src/services/SQLiteService/sqlite.service';
import { retry } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: 'app.component.html',
  styleUrls: ['app.component.scss'],
})
export class AppComponent {

  constructor(
    private platform: Platform,
  ) {
    this.initializeApp();
  }

  
  initializeApp() {
    this.platform.ready().then(() => {
      this.sqliteService.createTable().then(() => {
        // La tabla de credenciales se ha creado (si no existía) y ahora puedes agregar credenciales.
        this.agregarCredenciales();
      });
    });
    // this.agregarCredencial('Cam', '12345');
    // this.agregarCredencial('Diego', '12345');
    // retry(2);
  }
}
