import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {

  constructor(private router: Router) {}

  login() {
    // Aquí puedes agregar la lógica de autenticación si es necesario

    // Redirige al componente "Menu"
    this.router.navigate(['/menu']); // Asegúrate de que la ruta sea correcta
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>
      Blank
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Blank</ion-title>
    </ion-toolbar>
  </ion-header>

  <div id="container">
    <ion-input name="User" type="text" placeholder="Juanito"></ion-input>
    <ion-input name="Pass" type="password" placeholder="*****"></ion-input>
    <ion-button expand="full" (click)="login()">Login</ion-button>

  </div>
</ion-content>
