import { NgModule } from '@angular/core';
import { Routes, RouterModule } from '@angular/router';
import { PerfilComponent } from './menu/perfil/perfil.component';
import { MiCuentaComponent } from './menu/mi-cuenta/mi-cuenta.component';
import { AsignaturasComponent } from './menu/asignaturas/asignaturas.component';
import { NotFoundComponent } from './not-found/not-found.component';
import { HomePage } from './home/home.page'; // Asegúrate de importar la página de inicio de sesión
import { MenuPage } from './menu/menu.page';
import { MenuProfesorPage } from './menu-profesor/menu-profesor.page';
import { MenuResolverService } from './menu-resolver.service';

const routes: Routes = [
  { path: '', redirectTo: 'home', pathMatch: 'full' },
  {
    path: 'home',
    component: HomePage, // Página de inicio de sesión
  },
  {
    path: 'menu',
    component: MenuPage,
    resolve: { menuResolver: MenuResolverService } // Resolver para la página de menú
  },
  {
    path: 'menu-profesor',
    component: MenuProfesorPage,
    resolve: { menuResolver: MenuResolverService } // Resolver para la página de menú profesor
  },
  { path: 'forgot-password', loadChildren: () => import('./home/forgot/forgot-password.module').then(m => m.ForgotPasswordPageModule) },
  /* Componentes */
  {
    path: 'perfil',
    component: PerfilComponent
  },
  {
    path: 'cuenta',
    component: MiCuentaComponent
  },
  {
    path: 'asignaturas',
    component: AsignaturasComponent
  },
  /* Not Found */
  {
    path: 'not-found',
    component: NotFoundComponent, 
  },
  {
    path: '**', 
    redirectTo: 'not-found',
  },
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule],
})
export class AppRoutingModule {}
